const AddTask = () => {
    return (
        <Container>
            <Heading>{name}'s Tasks</Heading>

        </Container>
    )
}

export default AddTask

const Container = styled.div`
    width: 50%;
    height: 400px;
    background-color: orange;
    position: fixed;
    z-index: 100;
    top: 0;
    border-radius: 0px 0px 0px 0px;

    @media screen and (max-width: 800px) {
     {
         width: 100%;
    }
`

const Heading = styled.div`
    padding-left: 30px;
    font-size: 2em;
    font-weight: 800;
    width: 50%;
    min-width: 250px;
    height: 100%;
    line-height: 3em;
    color: #ffb347;
`
