        setInputValue(event.target.value);
      };

    const addTask = (e) => {
        e.preventDefault();
        console.log(inputValue);
        setInputValue("");
    }

    return (
        <Container>
            <form className = 'add-form'>
    <div className = 'control'>
        <label>Task</label>
